import {GET_POSTS,POST_ERROR,UPDATE_LIKES, DELETE_POST,ADD_POST,GET_POST,ADD_COMMENT,REMOVE_COMMENT} from '../action/Type';

const initialState = {
    posts:[],
    post: null,
    loading:true,
    error: {}
}

export default (state=initialState,action) => {
    const {type,payload} = action;
    console.log(payload);

    switch(type) {
        case GET_POSTS:
            return {
                ...state,posts:payload,loading:false
            };
        case GET_POST:
            return{
                ...state,post:payload,loading:false
            };
        case POST_ERROR:
            return {
                ...state,error:payload,loading:false
            };
        case UPDATE_LIKES:
            return{
                ...state,posts:state.posts.map(post => post._id === payload.postId? {...post,likes:payload.likes} : post),
                loading:false
            };
        case ADD_POST:
            return{
                ...state,posts:[payload,...state.posts],loading:false 
            }
        case DELETE_POST:
            return{
                ...state,posts:state.posts.filter(post => post._id !== payload),
                loading:false
            }
        case ADD_COMMENT:
            return {
                ...state,post:{...state.post,comment:payload},loading:false
            }  
        case REMOVE_COMMENT:
            return{
                ...state,post:{...state.post,comment:state.post.commments.filter(comment => comment._id !== payload)},loading:false
            }  
            default:
                return state; 
    }
}
