import React, { useState, Fragment, useEffect } from 'react';
import { connect } from 'react-redux';
import { Link, withRouter } from 'react-router-dom';
import { Header, Form, Button, Input, Icon } from 'semantic-ui-react';
import { createProfile, getCurrentProfile } from '../../action/profileAction';

const EditProfile = ({
  profile: { profile, loading },
  createProfile,
  getCurrentProfile,
  history
}) => {
  const [formData, setFormData] = useState({
    company: '',
    website: '',
    location: '',
    status: '',
    skills: '',
    githubusername: '',
    bio: '',
    twitter: '',
    facebook: '',
    linkedin: '',
    youtube: '',
    instagram: ''
  });

  const [displaySocilaInputs, toggleSocialInputs] = useState({
    display: false
  });

  useEffect(() => {
    getCurrentProfile();
      setFormData({
        company: loading || !profile ? '' : profile.company,
        website: loading || !profile.website? '': profile.website,
        location:loading || !profile.location? '': profile.location,
        status: loading || !profile.status? '': profile.status,
        skills: loading || !profile.skills? '': profile.skills,
        githubusername: loading || !profile.githubusername? '': profile.githubusername,
        bio: loading || !profile.bio? '': profile.bio,
        twitter: loading || !profile.social.twitter? '': profile.social.twitter,
        facebook: loading || !profile.social.facebook? '': profile.social.facebook,
        linkedin: loading || !profile.social.linkedin? '': profile.social.linkedin,
        youtube: loading || !profile.social.youtube? '': profile.social.youtube,
        instagram: loading || !profile.social.instagram? '': profile.social.instagram,
        
      });
  }, []);

  const {
    company,
    website,
    location,
    status,
    skills,
    githubusername,
    bio,
    twitter,
    facebook,
    linkedin,
    youtube,
    instagram
  } = formData;

  const onChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  const onSubmit = e => {
    e.preventDefault();
    console.log(formData);
    createProfile(formData, history,true);
  };
  return (
    <div className='ui container'>
      <Header as='h1' color='teal'>
        Edit Your Profile
      </Header>
      <Header as='h1' color='blue'>
        <i className='fa fa-user' />
        Let's get some information to make your profile stand out
      </Header>
      <small>* = required field</small>
      <Form>
        <Form.Field
          control='select'
          name='status'
          value={status}
          onChange={onChange}
        >
          <option value='0'>Select Professional Status</option>
          <option value='Developer'>Developer</option>
          <option value='Developer'>Developer</option>
          <option value='Developer'>Developer</option>
          <option value='Developer'>Developer</option>
          <option value='Developer'>Developer</option>
        </Form.Field>
        <Form.Field>
          <input
            name='company'
            placeholder='Company'
            value={company}
            onChange={onChange}
          />
          <small>Give us an idea of where you are at in your career</small>
        </Form.Field>
        <Form.Field>
          <input
            name='website'
            placeholder='Websit'
            value={website}
            onChange={onChange}
          />
          <small>Give us an idea of where you are at in your career</small>
        </Form.Field>
        <Form.Field>
          <input
            name='location'
            placeholder='Location'
            value={location}
            onChange={onChange}
          />
          <small>Give us an idea of where you are at in your career</small>
        </Form.Field>
        <Form.Field>
          <input
            name='skills'
            placeholder='* Skills'
            value={skills}
            onChange={onChange}
          />
          <small>Give us an idea of where you are at in your career</small>
        </Form.Field>
        <Form.Field>
          <input
            name='githubusername'
            placeholder='Githhub Username'
            value={githubusername}
            onChange={onChange}
          />
          <small>Give us an idea of where you are at in your career</small>
        </Form.Field>
        <Form.TextArea
          placeholder='bio data'
          name='bio'
          value={bio}
          onChange={onChange}
        />
        <Button
          type='submit'
          className='ui button teal '
          onClick={() => toggleSocialInputs({ display: true })}
        >
          Add Social Network Links
        </Button>

        {displaySocilaInputs.display && (
          <Fragment>
            <Form.Field
              control='input'
              name='twitter'
              type='text'
              placeholder='twitter URL'
              value={twitter}
              onChange={onChange}
            />
            <Form.Field
              control='input'
              name='facebook'
              type='text'
              placeholder='facebook URL'
              value={facebook}
              onChange={onChange}
            />
            <Form.Field
              control='input'
              name='youtube'
              type='text'
              placeholder='youtube URL'
              value={youtube}
              onChange={onChange}
            />
            <Form.Field
              control='input'
              name='linkedin'
              type='text'
              placeholder='linkedin URL'
              value={linkedin}
              onChange={onChange}
            />
            <Form.Field
              control='input'
              name='instagram'
              type='text'
              placeholder='instagram URL'
              value={instagram}
              onChange={onChange}
            />
          </Fragment>
        )}

        <Button type='submit' className='ui button teal' onClick={onSubmit}>
          Submit
        </Button>
      </Form>
    </div>
  );
};
const mapStateToProps = state => ({
  profile: state.profile
});

export default connect(mapStateToProps, { createProfile, getCurrentProfile })(
  withRouter(EditProfile)
);
