import {SET_ALERT,REMOVE_ALERT} from './Type';
import uuid from 'uuid';

export const setAlert = (msg,alertType) => dispatch =>{
    console.log(msg,)
    const id = uuid.v4();
    dispatch({
        type: SET_ALERT,
        payload:{msg,alertType,id}
    });
    setTimeout(() => {
        dispatch({
            type:REMOVE_ALERT,
            payload:id
        })
    }, 5000);

}
