import React from 'react';
import ReactDOM from 'react-dom';
import {Provider} from 'react-redux';
import thunk from 'redux-thunk';
import {createStore,applyMiddleware} from 'redux';

import App from './App';
import reducer from './component/reducer';


import {composWidthDevTools} from 'redux-devtools-extension';

const middlewares = [thunk];

const composeWidthEnhancer = composWidthDevTools(
    applyMiddleware(... middlewares)
);
// const composeEnhancers = window._REDUX_DEVTOOLS_EXTENSION_COMPOSE_ || compose;
// const store = createStore(
//     reducer,composeEnhancers(applyMiddleware(thunk))
// );


export const configureStore = createStore(reducer,composeWidthEnhancer);

ReactDOM.render(
    <Provider store={configureStore}>
        <App/>
    </Provider>
    ,document.querySelector('#root')
);
