import {GET_POST,POST_ERROR} from '../action/Type';

const initialState = {
    posts:[],
    post: null,
    loading:true,
    error: {}
}

export default (state=initialState,action) => {
    const {type,payload} = action;

    switch(type) {
        case GET_POST:
            return {
                ...state,posts:payload,loading:false
            }
            case GET_POST:
                return {
                    ...state,posts:payload,loading:false
                }
    }
}
